css:
.print-format {
    font-family: Arial, sans-serif;
    font-size: 13px;
    color: #000;
    padding: 20px;
}

.header {
    text-align: center;
    margin-bottom: 15px;
}

.header img {
    height: 60px;
    margin-bottom: 20px;
}

.org-name {
    font-size: 14px;
    margin-bottom: 20px;
}

.title {
    font-size: 18px;
    font-weight: bold;
    margin-top: 4px;
}

.doc-id {
    color: #777;
    font-size: 12px;
    margin-top: 2px;
}

hr {
    border-top: 1px solid #aaa;
    margin: 2px 0;
}

.data-table {
    width: 70%;
    margin: auto;
    border-collapse: collapse;
    /*margin-left: 100px;*/
}

.data-table td {
    padding: 8px 10px;
    vertical-align: top;
}

.data-table td.label {
    font-weight: bold;
    width: 40%;
    color: #333;
}

.signature-table {
    width: 100%;
    margin-top: 60px;
    text-align: center;
}

.signature-table td {
    padding-top: 100px;
    font-size: 12px;
    font-weight: bold;
}
